from Brain.brain_info import BRAIN_ARTICLES_WACOM, XP_PEN_PAGES, WACOM_PAGES, BRAIN_ARTICLES_XP_PENfrom Brain.brain_model import BrainStoredef start_brain_wacom():    brain = BrainStore(urls=WACOM_PAGES)    items = brain.generate_info_wacom()    return brain.compare_all_data(items, BRAIN_ARTICLES_WACOM)def start_brain_xp_pen():    brain = BrainStore(urls=XP_PEN_PAGES)    items = brain.generate_info_wacom()    return brain.compare_all_data(items, BRAIN_ARTICLES_XP_PEN)