import gspreadimport _sqlite3from datetime import datetimefrom Scraping_shoops.config import GOOGLE_TOKENfrom Classes.store import Itemclass DataBase:    __connection = _sqlite3.connect('Scraping_shoops/Foxtrot/Wacom_price.db')    __cursor = __connection.cursor()    items_from_db = []    def get_all_from_db(self):        self.__cursor.execute('''SELECT * FROM WACOM''')        all_from_table = self.__cursor.fetchall()        for elem in all_from_table:            item = Item.from_tuple(elem)            self.items_from_db.append(item)            yield item        self.__connection.close()        return self.items_from_db