class Item:    def __init__(self, article, title, price):        self.article = article        self.title = title        self.price = price    @classmethod    def from_tuple(cls, db_tuple):        if len(db_tuple):            return cls(db_tuple[0], db_tuple[1], db_tuple[2])        else:            raise ValueError(f"Invalid tuple length:  {db_tuple}")    def strip_price(self, price: str):        return price.strip().replace(' ', '').replace(',00', '')    @classmethod    def get_article_from_title(cls, title: str):        article = title.split('(')        article = article[1].replace(')', "")        return article