class Item:    def __init__(self, article, title, price):        # self.primary_key = primary_key        self.article = article        self.title = title        self.price = price    @classmethod    def from_tuple(cls, db_tuple):        return cls(db_tuple[1], db_tuple[2], db_tuple[3])    def strip_price(self, price: str):        return price.strip().replace(' ', '').replace(',00', '')    @classmethod    def get_article_from_title(self, title: str):        article = title.split('(')        article = article[1].replace(')', "")        return article