from Classes.store import BaseStore, Soupfrom utillities import get_article_from_titleclass ClickStore(BaseStore):    def __init__(self):        super().__init__(            shop_url='https://click.ua/shop/search?q=wacom')        self.soup = Soup(ClickStore.get(            self, shop_url='https://click.ua/shop/search?q=wacom'))        self.all_items = self.soup.find_element(class_='catalog__row')    def generate_info(self):        try:            for item in self.all_items.find_all(class_='product'):                price = item.find_next(class_="product__price").find('b', class_='b')                name = item.find_next(class_='product__title')                if price is not None:                    price = price.text.strip().split('â‚´')[0].strip().replace(' ', '')                    print(price)                if name is not None:                    name = name.text.strip()                card_item = {                    'name': name,                    'price': price,                    'article': get_article_from_title(name)                }                self.item_list.append(card_item)        except Exception as ex:            return f'{ex}'        return self.item_list