from Foxtrot.foxtrot_info import WACOM_URL, XP_PEN_URLfrom Foxtrot.shop_model import FoxtrotStoredef start_foxtrot():    foxtrot = FoxtrotStore(url=WACOM_URL)    items = foxtrot.generate_info()    return foxtrot.compare_data(items)def start_foxtrot_xp_pen():    foxtrot = FoxtrotStore(url=XP_PEN_URL)    items = foxtrot.generate_info()    return foxtrot.compare_data_xp_pen(items)