import requestsfrom bs4 import BeautifulSoupfrom Classes.store import BaseStore, Soupfrom utillities import get_article_from_title# need check ...class FoxtrotStore(BaseStore):    def __init__(self, ):        super().__init__(            shop_url='https://www.foxtrot.com.ua/uk/shop/graficheskie_plansheti_wacom.html'        )        self.response = requests.get('https://www.foxtrot.com.ua/uk/shop/graficheskie_plansheti_wacom.html')        self.bs = BeautifulSoup(self.response.text, 'lxml')        self.all_items = self.bs.find(class_='listing__body').find(class_='listing__body-wrap image-switch')    def generate_info(self):        for item in self.all_items.find_all('article'):            price = item.find_next('div', class_="card-price")            name = item.find_next(class_='card__title')            if price is not None:                price = price.text.strip().replace(" ", "").replace("â‚´", "")            if name is not None:                name = name.get('title')            card_item = {                'name': name,                'price': price,                'article': get_article_from_title(name)            }            self.item_list.append(card_item)        return self.item_list