from selenium import webdriverfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.chrome.service import Servicefrom selenium.webdriver.chrome.options import Optionsfrom webdriver_manager.chrome import ChromeDriverManagerfrom Classes.store import BaseStorefrom utillities import get_article_from_title, clean_priceclass RozetkaStore(BaseStore):    def __init__(self):        super().__init__(            shop_url='https://rozetka.com.ua/search/?producer=wacom&redirected=1&seller=rozetka&text=wacom'        )        self.item_list = []    def generate_info(self, url):        options = webdriver.ChromeOptions()        options.add_argument('--headless')        options.add_argument('--no-sandbox')        options.add_argument('--disable-dev-shm-usage')        options.add_argument('--disable-gpu')        options.add_argument('--remote-debugging-port=9222')        options.add_argument('--disable-extensions')        options.add_argument('--disable-application-cache')        options.add_argument('--disable-infobars')        driver = webdriver.Chrome(service=Service(ChromeDriverManager().install()), options=options)        try:            driver.get(url)            items = driver.find_elements(By.CSS_SELECTOR, 'li.catalog-grid__cell')            for item in items:                try:                    title_element = item.find_element(By.CSS_SELECTOR, 'a.product-link.goods-tile__heading')                    title_text = title_element.text.strip()                    article = get_article_from_title(title_text.strip('(Офіційна гарантія 24 міс.)'))                    price_new_elements = item.find_elements(By.CSS_SELECTOR, 'span.goods-tile__price-value')                    price_old_elements = item.find_elements(By.CSS_SELECTOR, 'div.goods-tile__price--old')                    status_elements = item.find_elements(By.CSS_SELECTOR, 'div.goods-tile__availability')                    available = True                    if status_elements:                        availability_text = status_elements[0].text                        if 'Немає в наявності' in availability_text:                            available = False                            continue                    price = 'N/A'                    if price_new_elements:                        price = price_new_elements[0].text                    elif price_old_elements:                        price = price_old_elements[0].text                    card_item = {                        'name': title_text.replace("Офіційна гарантія 24 міс.", ""),                        'price': clean_price(price) if price != 'N/A' else price,                        'article': article,                        'available': available                    }                    self.item_list.append(card_item)                except Exception as e:                    print(f'Ошибка при обработке элемента: {e}')        except Exception as e:            print(f'Ошибка загрузки страницы: {e}')        finally:            driver.quit()        return self.item_list