import requestsfrom bs4 import BeautifulSoupfrom Classes.store import BaseStorefrom utillities import get_article_from_title, clean_priceclass WoStore(BaseStore):    def __init__(self):        super().__init__(shop_url='https://wo.ua/ua/search/?q=wacom')        self.soup = self.get_soup('https://wo.ua/ua/search/?q=wacom')        self.all_items = self.soup.find(class_='filtered-items')    def get_soup(self, url):        response = requests.get(url)        response.raise_for_status()        return BeautifulSoup(response.text, 'lxml')    def generate_info(self):        for item in self.soup.find_all('div', class_='product-item ecom_item'):            price_element = item.find('div', class_='product-item__bottom-price-new')            name_element = item.find('a', class_='product-item__name')            if price_element and name_element:                price = price_element.text.strip().replace('.00', '')                name = name_element.text.strip()                article = get_article_from_title(name)                card_item = {                    'name': name,                    'price': clean_price(price),                    'article': article                }                self.item_list.append(card_item)        return self.item_list