import asyncioimport loggingimport osfrom dotenv import load_dotenvfrom aiogram import Bot, Dispatcher, typesfrom aiogram.filters import Commandfrom telegram_bot.handlers import user_routerfrom telegram_bot.keyboards import kb,  get_custom_keyboardload_dotenv()token = os.getenv(key='BOT_TOKEN')bot = Bot(token=token)dp = Dispatcher(bot=bot)dp.include_router(user_router)logging.basicConfig(level=logging.INFO)@dp.message(Command(commands=["start"]))async def start(message: types.Message):    await message.answer(f"Привіт, {message.from_user.username}!✋:")    await message.answer(f"Тисни на кнопку  ⬇️ ⬇️ ⬇️", reply_markup=get_custom_keyboard())async def main():    await dp.start_polling(bot, skip_updates=True)if __name__ == "__main__":    asyncio.run(main())