import asyncioimport loggingimport osfrom dotenv import load_dotenvfrom aiogram import Bot, Dispatcher, typesfrom aiogram.filters import Commandfrom Foxtrot.foxtrot import start_foxtrotfrom KTC.ktc import start_ktcfrom Moyo.moyo import start_moyofrom Click.click import start_clickload_dotenv()token = os.getenv(key='BOT_TOKEN')bot = Bot(token=token)dp = Dispatcher(bot=bot)logging.basicConfig(level=logging.INFO)@dp.message(Command(commands=["start"]))async def start(message: types.Message):    kb = [        [types.KeyboardButton(text="ğŸ¦ŠFoxtrotğŸ¦Š")],        [types.KeyboardButton(text="Moyo")],        [types.KeyboardButton(text="KTC")],        [types.KeyboardButton(text="Click")]    ]    keyboard = types.ReplyKeyboardMarkup(keyboard=kb, resize_keyboard=True)    await message.answer(f"ĞŸÑ€Ğ¸Ğ²Ñ–Ñ‚, {message.from_user.username}!âœ‹:")    await message.answer(f"Ğ¢Ğ¸ÑĞ½Ğ¸ Ğ½Ğ° ĞºĞ½Ğ¾Ğ¿ĞºÑƒ  â¬‡ï¸ â¬‡ï¸ â¬‡ï¸", reply_markup=keyboard)@dp.message()async def handle_message(message: types.Message):    if message.text == "ğŸ¦ŠFoxtrotğŸ¦Š":        results = start_foxtrot()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"ğŸ›’Foxtrot:\n\n{result_str}")    elif message.text == "Moyo":        results = start_moyo()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"ğŸ›’ Moyo:\n\n{result_str}")    elif message.text == "KTC":        results = start_ktc()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"ğŸ›’ KTC:\n\n{result_str}")    elif message.text == "Click":        results = start_click()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"ğŸ›’ Click:\n\n{result_str}")    else:        await message.answer("Ğ¾Ğ±ĞµÑ€Ğ¸ Ğ¼Ğ°Ğ³Ğ°Ğ·Ğ¸Ğ½")async def main():    await dp.start_polling(bot, skip_updates=True)if __name__ == "__main__":    asyncio.run(main())