import asyncioimport loggingimport osfrom dotenv import load_dotenvfrom aiogram import Bot, Dispatcher, typesfrom aiogram.filters import Commandfrom Foxtrot.foxtrot import start_foxtrotfrom KTC.ktc import start_ktcfrom Moyo.moyo import start_moyofrom Click.click import start_clickload_dotenv()token = os.getenv(key='BOT_TOKEN')bot = Bot(token=token)dp = Dispatcher(bot=bot)logging.basicConfig(level=logging.INFO)@dp.message(Command(commands=["start"]))async def start(message: types.Message):    kb = [        [types.KeyboardButton(text="🦊Foxtrot🦊")],        [types.KeyboardButton(text="Moyo")],        [types.KeyboardButton(text="KTC")],        [types.KeyboardButton(text="Click")]    ]    keyboard = types.ReplyKeyboardMarkup(keyboard=kb, resize_keyboard=True)    await message.answer(f"Привіт, {message.from_user.username}!✋:")    await message.answer(f"Тисни на кнопку  ⬇️ ⬇️ ⬇️", reply_markup=keyboard)@dp.message()async def handle_message(message: types.Message):    if message.text == "🦊Foxtrot🦊":        results = start_foxtrot()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"🛒Foxtrot:\n\n{result_str}")    elif message.text == "Moyo":        results = start_moyo()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"🛒 Moyo:\n\n{result_str}")    elif message.text == "KTC":        results = start_ktc()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"🛒 KTC:\n\n{result_str}")    elif message.text == "Click":        results = start_click()        result_str = '\n'.join([str(item) for item in results])        await message.answer(f"🛒 Click:\n\n{result_str}")    else:        await message.answer("обери магазин")async def main():    await dp.start_polling(bot, skip_updates=True)if __name__ == "__main__":    asyncio.run(main())