import loggingfrom Classes.db import DataBasefrom databases.parse_db import GoogleSheet, db_path, get_info_from_dbfrom tasks.client import celery_appdb = DataBase(db_path=db_path)google_sheet = GoogleSheet()logging.basicConfig(level=logging.INFO)@celery_app.taskdef update_db(table_url):    try:        table = GoogleSheet()        data = table.generate_info_from_google_sheet_list(google_sheet_url=table_url)        cleaned_data = table.clear_info_from_sheets_lists(data)        db.update_db(cleaned_data)    except Exception as e:        logging.error(f"Database was not updated: {e}")