import jsonimport osimport redisfrom dotenv import load_dotenvload_dotenv()class RedisClient:    def __init__(self):        self.redis_client = redis.Redis(host=os.getenv('REDIS_ADRESS'),                                        port=os.getenv('REDIS_PORT'),                                        db=0)    def save_data_in_redis(self, name, value, time=3600):        self.redis_client.set(name=name, value=value, ex=time)        return f'Saved to Redis'    def transfer_to_json(self, data: list):        return json.dumps(data)    def get_data_from_redis(self, name):        return self.redis_client.get(name=name)redis_client = RedisClient()