import jsonimport redisclass RedisClient:    def __init__(self, host: str, port: int, db: int):        self.redis_client = redis.Redis(host=host, port=port, db=db)    def save_data_in_redis(self, name, value, time=3600):        self.redis_client.set(name=name, value=value, ex=time)        return f'Saved to Redis'    def transfer_to_json(self, data: list):        return json.dumps(data)    def get_data_from_redis(self, name):        return self.redis_client.get(name=name)redis_client = RedisClient(host='localhost', port=6379, db=0)