import jsonfrom Classes.status import Statusfrom Classes.store import BaseStore, Scraperfrom stores.Auchan.auchan_info import AUCHAN_ARTICLESfrom telegram_bot.bot import send_telegram_message_taskclass AuchanStore(BaseStore):    def __init__(self, url):        super().__init__(shop_url=url)        self.scraper = Scraper()    import json    def generate_info(self, title_locator=None, price_locator=None, status_locator=None):        response = self.scraper.get(self.url)        item_list = []        try:            data = json.loads(response.text)            items = data.get("data", {}).get("search", {}).get("items", [])            for product in items:                name = product.get('name', '').strip()                price = product['price_range']['minimum_price']['final_price']['value']                status = Status.in_stock if product.get('stock_status', '') == "IN_STOCK" else Status.not_in_stock                article = AUCHAN_ARTICLES.get(name)                card_item = {                    'name': name,                    'price': price,                    'article': article,                    'status': status,                }                item_list.append(card_item)        except Exception as e:            print(f"Ошибка при обработке данных: {e}")        return item_list