import loggingimport osimport djangofrom scraper.celery_config import appos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from stores.Auchan.auchan_model import AuchanStorefrom stores.Auchan.locators import AuchanLocatorlogger = logging.getLogger('scraper')@app.task()def start_auchan_wacom():    try:        auchan = AuchanStore(url=AuchanLocator.WACOM_PAGE_URL)        items = auchan.generate_info()        if not items:            logger.info(f"Не вдалось спарсити данні Auchan/WACOM - {len(items)} товарів")            auchan.send_allert_notification()        auchan.save_parsed_data(partner_name="Auchan", items=items, brand="WACOM")        logger.info(f"Збережено/Оновлено {len(items)} товарів для партнера Auchan (бренд WACOM).")    except Exception as e:        logger.info(f"Помилка парсингу данних Auchan/WACOM {e}")