import osimport djangofrom scraper.celery_config import appfrom stores.Auchan.auchan_model import AuchanStorefrom stores.Auchan.locators import AuchanLocatoros.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()@app.task()def start_auchan_wacom():    auchan = AuchanStore(url=AuchanLocator.WACOM_PAGE_URL)    items = auchan.generate_info()    auchan.save_parsed_data(partner_name="Auchan", items=items)