import osimport djangofrom scraper.celery_config import appos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from stores.Auchan.auchan_model import AuchanStorefrom stores.Auchan.locators import AuchanLocatorfrom telegram_bot.bot import send_telegram_message_task@app.task()def start_auchan_wacom():    auchan = AuchanStore(url=AuchanLocator.WACOM_PAGE_URL)    items = auchan.generate_info()    if not items:        auchan.send_allert_notification()    else:        auchan.save_parsed_data(partner_name="Auchan", items=items,  brand="WACOM")