from redis_client.redis_client import redis_clientfrom stores.Brain.brain_info import BRAIN_ARTICLES_WACOM, XP_PEN_PAGES, WACOM_PAGES, BRAIN_ARTICLES_XP_PENfrom stores.Brain.brain_model import BrainStorefrom tasks.client import celery_app@celery_app.taskdef start_brain_wacom():    brain = BrainStore(urls=WACOM_PAGES)    items = brain.generate_info_wacom()    result = brain.compare_all_data(items, BRAIN_ARTICLES_WACOM)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='brain_wacom', value=json_data)    return result@celery_app.taskdef start_brain_xp_pen():    brain = BrainStore(urls=XP_PEN_PAGES)    items = brain.generate_info_wacom()    result = brain.compare_all_data(items, BRAIN_ARTICLES_XP_PEN)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='brain_xp', value=json_data)    return resultprint(start_brain_xp_pen())