from scraper.celery_config import appfrom stores.Brain.brain_info import BRAIN_ARTICLES_WACOM, XP_PEN_PAGES, WACOM_PAGES, BRAIN_ARTICLES_XP_PENfrom stores.Brain.brain_model import BrainStore@app.task()def start_brain_wacom():    brain = BrainStore(urls=WACOM_PAGES)    items = brain.generate_info_wacom(BRAIN_ARTICLES_WACOM)    return items@app.task()def start_brain_xp_pen():    brain = BrainStore(urls=XP_PEN_PAGES)    items = brain.generate_info_wacom(BRAIN_ARTICLES_XP_PEN)    return items