from redis_client.redis_client import redis_clientfrom stores.CAN.can_info import wacom_pages_urls, CAN_WACOM_ARICLES, xp_pen_pages_urls, CAN_XP_PEN_ARRICLESfrom stores.CAN.can_model import CanStoredef start_can_wacom():    can = CanStore(wacom_pages_urls)    items = can.generate_info()    result = can.compare_all_data(partner_list=items, articles_list=CAN_WACOM_ARICLES)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='can_wacom', value=json_data)    return resultdef start_can_xp_pen():    can = CanStore(xp_pen_pages_urls)    items = can.generate_info()    result = can.compare_all_data(partner_list=items, articles_list=CAN_XP_PEN_ARRICLES)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='can_xp_pen', value=json_data)    return result