from scraper.celery_config import appfrom stores.CAN.can_info import wacom_pages_urls, CAN_WACOM_ARICLES, xp_pen_pages_urls, CAN_XP_PEN_ARRICLESfrom stores.CAN.can_model import CanStore@app.task()def start_can_wacom():    can = CanStore(wacom_pages_urls)    items = can.generate_info_with_using_articles_dict(CAN_WACOM_ARICLES)    can.save_parsed_data(partner_name="CAN", items=items,  brand="WACOM")@app.task()def start_can_xp_pen():    can = CanStore(xp_pen_pages_urls)    items = can.generate_info_with_using_articles_dict(CAN_XP_PEN_ARRICLES)    can.save_parsed_data(partner_name="CAN", items=items, brand="XP-Pen")