from redis_client.redis import redis_clientfrom stores.Citrus.citrus_model import CitrusStorefrom tasks.client import celery_app@celery_app.taskdef start_citrus():    citrus = CitrusStore()    items = citrus.generate_info()    result = citrus.compare_data_citrus(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='citrus_wacom', value=json_data)    return result