import osimport djangofrom redis_client.redis_client import redis_clientfrom stores.Citrus.citrus_config import CITRUS_ITEMSfrom stores.Citrus.citrus_model import CitrusStorefrom stores.Citrus.locators import CitrusLocatoros.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from items.models import Itemdef start_citrus_wacom():    citrus = CitrusStore(url=CitrusLocator.WACOM_PAGE_URL)    items = citrus.generate_info()    result = citrus.compare_data_xp_pen(        partner_items_list=items,        article_dict=CITRUS_ITEMS,        model=Item    )    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='citrus_wacom', value=json_data)    return result