import osimport djangofrom scraper.celery_config import appfrom stores.Citrus.citrus_model import CitrusStorefrom stores.Citrus.locators import CitrusLocatoros.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()@app.task()def start_citrus_wacom():    citrus = CitrusStore(url=CitrusLocator.WACOM_PAGE_URL)    items = citrus.generate_info()    citrus.save_parsed_data(partner_name="Citrus", items=items)@app.task()def start_citrus_xp_pen():    citrus = CitrusStore(url=CitrusLocator.XP_PEN_PAGE_URL)    items = citrus.generate_info_xp_pen()    citrus.save_parsed_data(partner_name="Citrus", items=items)