import requestsfrom bs4 import BeautifulSoupfrom Classes.status import Statusfrom Classes.store import BaseStorefrom items.models import Itemfrom stores.Click.click_info import CLICK_ARTICLESfrom utillities import get_article_from_titleclass ClickStore(BaseStore):    def __init__(self, url):        super().__init__(            shop_url='https://click.ua/shop/search?q=wacom')        self.item_list = []        self.bs = requests.get(url=url)        self.soup = BeautifulSoup(self.bs.text, 'lxml')        self.all_items = self.soup.find(class_='catalog__row')    def generate_info(self, article_dict):        try:            item_dict = {}  # Словарь для устранения дубликатов            for item in self.all_items.find_all(class_='product'):                price_elem = item.find_next(class_="product__price").find('b', class_='b')                name_elem = item.find_next(class_='product__title')                status_elem = item.find_next(class_='fs12 color --green')                # Извлечение данных                price = (                    price_elem.text.strip().split('₴')[0].strip().replace(' ', '')                    if price_elem else None                )                name = name_elem.text.strip() if name_elem else None                status = Status.in_stock if status_elem else Status.not_in_stock                # Если название отсутствует, пропускаем товар                if not name:                    continue                # Создание карточки товара                card_item = {                    'name': name,                    'price': price,                    'article': article_dict.get(name),                    'status': status                }                # Условие для сохранения только товаров "в наличии"                if name in item_dict:                    # Перезаписываем только если новый статус "в наличии"                    if status == Status.in_stock:                        item_dict[name] = card_item                else:                    item_dict[name] = card_item            # Преобразуем словарь обратно в список            self.item_list = list(item_dict.values())        except Exception as ex:            return f'{ex}'        return self.item_list