import requestsfrom bs4 import BeautifulSoupfrom Classes.status import Statusfrom Classes.store import BaseStoreclass ClickStore(BaseStore):    def __init__(self, url):        super().__init__(            shop_url='https://click.ua/shop/search?q=wacom')        self.item_list = []        self.bs = requests.get(url=url)        self.soup = BeautifulSoup(self.bs.text, 'lxml')        self.all_items = self.soup.find(class_='catalog__row')    def generate_info(self, article_dict):        try:            item_dict = {}            for item in self.all_items.find_all(class_='product'):                price_elem = item.find_next(class_="product__price").find('b', class_='b')                name_elem = item.find_next(class_='product__title')                status_elem = item.find_next(class_='fs12 color --green')                price = (                    price_elem.text.strip().split('â‚´')[0].strip().replace(' ', '')                    if price_elem else None                )                name = name_elem.text.strip() if name_elem else None                status = Status.in_stock if status_elem else Status.not_in_stock                if not name:                    continue                card_item = {                    'name': name,                    'price': price,                    'article': article_dict.get(name),                    'status': status                }                if name in item_dict:                    if status == Status.in_stock:                        item_dict[name] = card_item                else:                    item_dict[name] = card_item            self.item_list = list(item_dict.values())        except Exception as ex:            return f'{ex}'        return self.item_list