from redis_client.redis_client import redis_clientfrom stores.Click.click_info import wacom_page_url, xp_pen_page_urlfrom stores.Click.click_model import ClickStoredef start_click_wacom():    click = ClickStore(wacom_page_url)    items = click.generate_info()    result = click.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='click_wacom', value=json_data)    return resultdef start_click_xp_pen():    click = ClickStore(xp_pen_page_url)    items = click.generate_info()    result = click.compare_data_xp_pen(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='click_xp_pen', value=json_data)    return result