import osimport djangoos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from scraper.celery_config import appfrom stores.Click.click_info import wacom_page_url, xp_pen_page_url, CLICK_ARTICLES, WACOM_ARTICLESfrom stores.Click.click_model import ClickStore@app.task()def start_click_wacom():    click = ClickStore(wacom_page_url)    items = click.generate_info(article_dict=WACOM_ARTICLES)    click.save_parsed_data(partner_name="Click", items=items, brand="WACOM")@app.task()def start_click_xp_pen():    click = ClickStore(xp_pen_page_url)    items = click.generate_info(article_dict=CLICK_ARTICLES)    click.save_parsed_data(partner_name="Click", items=items, brand="XP-Pen")