from scraper.celery_config import appfrom stores.Click.click_info import wacom_page_url, xp_pen_page_urlfrom stores.Click.click_model import ClickStore@app.task()def start_click_wacom():    click = ClickStore(wacom_page_url)    items = click.generate_info()    click.save_parsed_data(partner_name="Click", items=items)@app.task()def start_click_xp_pen():    click = ClickStore(xp_pen_page_url)    items = click.generate_info()    click.save_parsed_data(partner_name="Click", items=items)