from redis_client.redis_client import redis_clientfrom stores.Comfy.comfy_info import ComfyLocatorsfrom stores.Comfy.comfy_model import ComfyStoredef start_comfy_wacom():    comfy = ComfyStore()    items = comfy.generate_info(url=ComfyLocators.wacom_url)    return itemsdef start_comfy_xp_pen():    comfy = ComfyStore()    items = comfy.generate_info(url=ComfyLocators.xp_pen_url)    result = comfy.compare_data_xp_pen(items, ComfyLocators.COMFY_XP_PEN_ARTICLES)    data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='comfy_xp', value=data)    return result## items = (start_comfy_wacom())# name_to_article = {item['name']: item['article'] for item in items}## # Результат# print(name_to_article)