import osimport djangoos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()import jsonimport loggingimport requestsfrom Classes.status import Statusfrom Classes.store import BaseStorelogger = logging.getLogger('scraping')class ComfyStore(BaseStore):    def __init__(self):        super().__init__(            shop_url='https://comfy.ua/ua/search/?q=wacom'        )        self.item_list = []        self.url = 'https://im.comfy.ua/api/products/list?storeId=5&cityId=506'    def generate_info(self, articles_dict, payload ):        try:            response = requests.post(url=self.url, data=json.dumps(payload))            response.raise_for_status()            all_items = response.json().get("items", [])            for item in all_items:                title = item.get("name", "").strip()                price = item.get("prices", {}).get("price", "")                article = articles_dict.get(title)                status_elem = item.get("remains", []).get('flags', []).get('isAvailable')                status = Status.in_stock if status_elem == True else Status.not_in_stock                if "Мікропошкодження" in title:                    continue                card_item = {                    'name': title,                    'price': price,                    'article': article,                    'status': status                }                self.item_list.append(card_item)        except requests.RequestException as e:            print(f'Ошибка загрузки страницы: {e}')        except Exception as e:            print(f'Ошибка обработки элемента: {e}')        return self.item_list