from redis_client.redis_client import redis_clientfrom stores.Comtrading.comtrading_model import ComtradingStoreurl = 'https://api2.comtrading.ua/search?lang=ua'payload_wacom = {"query": "wacom"}payload_xp_pen = {"query": "xp-pen"}def start_comtrading_wacom():    comtrading = ComtradingStore(url, payload_wacom)    items = comtrading.generate_info()    result = comtrading.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='comtrading_wacom', value=json_data)    return resultdef start_comtrading_xp_pen():    comtrading = ComtradingStore(url, payload_xp_pen)    items = comtrading.generate_info()    result = comtrading.compare_data_xp_pen(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='comtrading_xp_pen', value=json_data)    return result