from stores.Comtrading.comtrading_model import ComtradingStoreurl = 'https://api2.comtrading.ua/search?lang=ua'payload_wacom = {"query": "wacom"}payload_xp_pen = {"query": "xp-pen"}def start_comtrading_wacom():    comtrading = ComtradingStore(url, payload_wacom)    items = comtrading.generate_info()    return comtrading.compare_data(items)def start_comtrading_xp_pen():    comtrading = ComtradingStore(url, payload_xp_pen)    items = comtrading.generate_info()    return comtrading.compare_data_xp_pen(items)