from scraper.celery_config import appfrom stores.Comtrading.comtrading_info import COMTRADING_ARTICLES_WACOM, COMTRADING_ARTICLES_XP_PENfrom stores.Comtrading.comtrading_model import ComtradingStoreurl = 'https://api2.comtrading.ua/search?lang=ua'payload_wacom = {"query": "wacom", 'size': 100}payload_xp_pen = {"query": "xp-pen", 'size': 100}@app.task()def start_comtrading_wacom():    comtrading = ComtradingStore(url, payload_wacom)    items = comtrading.generate_info(articles_dict=COMTRADING_ARTICLES_WACOM)    comtrading.save_parsed_data(partner_name="Comtrading", items=items,  brand="WACOM")@app.task()def start_comtrading_xp_pen():    comtrading = ComtradingStore(url, payload_xp_pen)    items = comtrading.generate_info(articles_dict=COMTRADING_ARTICLES_XP_PEN)    comtrading.save_parsed_data(partner_name="Comtrading", items=items,  brand="XP-Pen")