from redis_client.redis_client import redis_clientfrom stores.EXE.exe_info import WACOM_PAGE_URLS, XP_PEN_PAGES_URL, EXE_ARTICLESfrom stores.EXE.exe_model import ExeStorefrom items.models import Itemfrom tasks.client import celery_appdef start_exe_wacom():    exe = ExeStore(urls=WACOM_PAGE_URLS)    items = exe.generate_info_wacom()    result = exe.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='exe_wacom', value=json_data)    return result@celery_app.taskdef start_exe_xp_pen():    exe = ExeStore(urls=XP_PEN_PAGES_URL)    items = exe.generate_info_wacom()    result = exe.compare_data_xp_pen(items, EXE_ARTICLES, Item)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='exe_xp_pen', value=json_data)    return result