from redis_client.redis import redis_clientfrom stores.EXE.exe_info import WACOM_PAGE_URLS, XP_PEN_PAGES_URLfrom stores.EXE.exe_model import ExeStorefrom tasks.client import celery_app#@celery_app.taskdef start_exe_wacom():    exe = ExeStore(urls=WACOM_PAGE_URLS)    items = exe.generate_info_wacom()    result = exe.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='exe_wacom', value=json_data)    return result#@celery_app.taskdef start_exe_xp_pen():    exe = ExeStore(urls=XP_PEN_PAGES_URL)    items = exe.generate_info_wacom()    result = exe.compare_data_xp_pen(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='exe_xp_pen', value=json_data)    return result