from redis_client.redis import redis_clientfrom stores.F.fotos_model import FStorefrom tasks.client import celery_app@celery_app.taskdef start_fotos():    f = FStore(shop_url='https://f.ua/ua/shop/graficheskie-planshety/wacom/?sticker=fua')    items = f.generate_info('https://f.ua/ua/shop/graficheskie-planshety/wacom/?sticker=fua')    result = f.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='fotos_wacom', value=json_data)    return result@celery_app.taskdef start_fotos_xp_pen():    f = FStore(shop_url='https://f.ua/ua/shop/graficheskie-planshety/xp-pen/?sticker=fua')    items = f.generate_info('https://f.ua/ua/shop/graficheskie-planshety/xp-pen/?sticker=fua')    result = f.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='fotos_xp_pen', value=json_data)    return result