from selenium import webdriverfrom selenium.webdriver.common.by import Byfrom Classes.status import Statusfrom Classes.store import BaseStorefrom utillities import get_article_from_titlefrom playwright.sync_api import sync_playwrightclass FStore(BaseStore):    def __init__(self, shop_url):        super().__init__(shop_url)    def generate_info(self, url, articles_dict):        item_list = []        with sync_playwright() as p:            browser = p.chromium.launch(headless=True)            context = browser.new_context()            page = context.new_page()            try:                page.goto(url)                elements = page.query_selector_all('.catalog_item')                for item in elements:                    try:                        name = item.query_selector('.title').inner_text()                        price_text = item.query_selector('.price').inner_text()                        article = articles_dict.get(name)                        status_elem = item.query_selector('.price_info_out_of_stock ')                        prices = [price.strip('₴') for price in price_text.replace(' ', '').split() if price.strip()]                        if len(prices) == 2:                            correct_price = prices[1]                        elif len(prices) == 1:                            correct_price = prices[0]                        else:                            correct_price = None                        if status_elem:                            status = Status.not_in_stock                        else:                            status = Status.in_stock                        card_item = {                            'name': name,                            'price': correct_price if correct_price != 'немаєвнаявності' else 0,                            'article': article,                            'status': status                        }                        item_list.append(card_item)                    except Exception as e:                        print(f'Ошибка при обработке элемента: {e}')            except Exception as e:                print(f'Ошибка загрузки страницы: {e}')            finally:                browser.close()        return item_list