from scraper.celery_config import appfrom stores.F.fotos_config import F_ARTICLES_WACOM, F_ARTICLES_XP_PENfrom stores.F.fotos_model import FStore@app.task()def start_fotos():    f = FStore(shop_url='https://f.ua/ua/shop/graficheskie-planshety/wacom/?sticker=fua')    items = f.generate_info('https://f.ua/ua/shop/graficheskie-planshety/wacom/?sticker=fua',                            F_ARTICLES_WACOM)    if not items:        f.send_allert_notification()    f.save_parsed_data(partner_name='Fotos', items=items, brand='WACOM')@app.task()def start_fotos_xp_pen():    f = FStore(shop_url='https://f.ua/ua/shop/graficheskie-planshety/xp-pen/?sticker=fua')    items = f.generate_info('https://f.ua/ua/shop/graficheskie-planshety/xp-pen/?sticker=fua',                            F_ARTICLES_XP_PEN)    if not items:        f.send_allert_notification()    f.save_parsed_data(partner_name='Fotos', items=items, brand='XP-Pen')