from redis_client.redis import redis_clientfrom stores.Foxtrot.foxtrot_info import WACOM_URL, XP_PEN_URLfrom stores.Foxtrot.shop_model import FoxtrotStorefrom tasks.client import celery_app#@celery_app.taskdef start_foxtrot_wacom():    foxtrot = FoxtrotStore(url=WACOM_URL)    items = foxtrot.generate_info()    result = foxtrot.compare_data(items)    data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='foxtrot', value=data)    return result#@celery_app.taskdef start_foxtrot_xp_pen():    foxtrot = FoxtrotStore(url=XP_PEN_URL)    items = foxtrot.generate_info()    result = foxtrot.compare_data(items)    data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='foxtrot_xp', value=data)    return result