from scraper.celery_config import appfrom stores.Foxtrot.foxtrot_info import WACOM_URL, XP_PEN_URL, FOXTROT_ARTICLES_WACOM, FOXTROT_ARTICLESfrom stores.Foxtrot.shop_model import FoxtrotStore@app.task()def start_foxtrot_wacom():    foxtrot = FoxtrotStore(url=WACOM_URL)    items = foxtrot.generate_info(articles_dict=FOXTROT_ARTICLES_WACOM)    return items@app.task()def start_foxtrot_xp_pen():    foxtrot = FoxtrotStore(url=XP_PEN_URL)    items = foxtrot.generate_info(articles_dict=FOXTROT_ARTICLES)    return items