from redis_client.redis import redis_clientfrom stores.Itbox.itbox_info import WACOM_PAGE_URLS, XP_PEN_PAGE_URLS, XP_PEN_ACTICLESfrom stores.Itbox.itbox_model import ItboxStorefrom tasks.client import celery_app@celery_app.taskdef start_itbox_wacom():    itbox = ItboxStore(WACOM_PAGE_URLS)    items = itbox.generate_info()    result = itbox.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='itbox_wacom', value=json_data)    return result@celery_app.taskdef start_itbox_xp_pen():    itbox = ItboxStore(XP_PEN_PAGE_URLS)    items = itbox.generate_info()    result = itbox.compare_all_data(items, XP_PEN_ACTICLES)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='itbox_xp_pen', value=json_data)    return result