import requestsfrom bs4 import BeautifulSoupfrom Classes.store import BaseStorefrom databases.db_helper import get_info_from_db, get_sessionfrom stores.Itbox.itbox_info import ITBOX_HEADERSfrom utillities import get_article_from_titleclass ItboxStore(BaseStore):    def __init__(self, urls):        super().__init__(            shop_url='https://www.itbox.ua/ua/search/?Search=wacom')        self.all_items = []        self.urls = urls        for url in self.urls:            soup = self.get_soup(url)            catalog_content = soup.find('div', class_='products')            if catalog_content:                self.all_items.append(catalog_content)            else:                print(f'No filtered-items found on page: {url}')    def get_soup(self, url):        response = requests.get(url=url,headers=ITBOX_HEADERS)        response.raise_for_status()        return BeautifulSoup(response.text, 'lxml')    def generate_info(self):        for items in self.all_items:            if items:                for item in items.find_all(class_='stuff left'):                    price = item.find_next(class_="stuff-price__digits")                    name = item.find_next('div', class_='stuff-desc').find('a', class_='to_product')                    article = get_article_from_title(name)                    if price is not None:                        price = price.text.strip().split('₴')[0].strip().replace(' ', '')                    if name is not None:                        name = name.text.strip().replace('(2021)', '')                    card_item = {                        'name': name,                        'price': price,                        'article': get_article_from_title(name)                    }                    self.item_list.append(card_item)        return self.item_list    def compare_all_data(cls, partner_list, articles_list):        items_from_db = list(get_info_from_db(session=get_session()))        items_dict = {item.article: item for item in items_from_db}        missing_items = []        for elem in partner_list:            name = elem['name']            price_partner = int(elem['price']) if elem['price'] is not None else 1            article = articles_list.get(name)            if article in items_dict.keys():                item = items_dict[article]                item_price = int(item.price.decode('utf-8')) if isinstance(item.price, bytes) else int(item.price)                if price_partner == item_price:                    missing_items.append(f'✅ {article} - Ціна партнера: {price_partner} грн, РРЦ: {item_price} грн')                elif price_partner < item_price:                    missing_items.append(                        f'🛑 Ціна нижча за РРЦ {article} - {price_partner} грн, Ціна РРЦ: {item_price} грн')                else:                    missing_items.append(                        f'⚠️ Ціна вища за РРЦ {article} - {price_partner} грн, Ціна РРЦ: {item_price} грн')            else:                missing_items.append(f'🔍{article} незнайдено в базі данних')        sorted_items = sorted(missing_items, key=lambda x: (not x.startswith('✅'), x))        return sorted_items