import osimport djangofrom scraper.celery_config import appos.environ["DJANGO_SETTINGS_MODULE"] = "scraper.settings"django.setup()from redis_client.redis_client import redis_clientfrom stores.Itbox.itbox_info import WACOM_PAGE_URLS, XP_PEN_PAGE_URLS, XP_PEN_ACTICLESfrom stores.Itbox.itbox_model import ItboxStore@app.taskdef start_itbox_wacom():    itbox = ItboxStore(WACOM_PAGE_URLS)    items = itbox.generate_info()    if not items:        itbox.send_allert_notification()    itbox.save_parsed_data(partner_name="ITBOX", items=items, brand="WACOM")@app.taskdef start_itbox_xp_pen():    itbox = ItboxStore(XP_PEN_PAGE_URLS)    items = itbox.generate_info()    if not items:        itbox.send_allert_notification()    itbox.save_parsed_data(partner_name="ITBOX", items=items, brand="XP-Pen")