from redis_client.redis import redis_clientfrom stores.KTC.ktc_model import KtcStorefrom tasks.client import celery_app#@celery_app.taskdef start_ktc():    ktc = KtcStore('https://ktc.ua/search/brand-wacom/graphic_tablets/?q=wacom&sort-search-desc')    items = ktc.generate_info()    result = ktc.compare_data(items)    data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='ktc', value=data)    return result#@celery_app.taskdef start_ktc_xp_pen():    ktc = KtcStore('https://ktc.ua/search/graphic_tablets/?q=xp-pen&sort-search-desc')    items = ktc.generate_info_xp_pen()    result = ktc.compare_data_xp_pen(items)    data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='ktc_xp', value=data)    return result