from scraper.celery_config import appimport osimport djangoos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from stores.MTA.mta_config import MTA_ITEMS, MTA_ITEMS_XP_PENfrom stores.MTA.mta_model import MtaStore@app.task()def start_mta_wacom():    mta = MtaStore()    items = mta.scrape_items(url_query_part='wacom', manufactures_part_of_url='wacom',                             url_pages_count=3, articles_dict=MTA_ITEMS)    # if not items:    #     mta.send_allert_notification()    # mta.save_parsed_data(partner_name='MTA', items=items, brand='WACOM')    return items@app.task()def start_mta_xp_pen():    mta = MtaStore()    items = mta.scrape_items(manufactures_part_of_url='xp-pen', url_query_part='xp-pen',                             url_pages_count=4, articles_dict=MTA_ITEMS_XP_PEN)    if not items:        mta.send_allert_notification()    mta.save_parsed_data(partner_name='MTA', items=items, brand='XP-Pen')