from Classes.store import Soup, BaseStorefrom utillities import clean_price, get_article_from_titleclass MoyoStore(BaseStore):    def __init__(self, urls):        super().__init__(            shop_url='https://www.moyo.ua/ua/acsessor/acsessor_for_comp/manipulators/wacom/?page=1')        self.urls = urls        self.all_items = []        for url in self.urls:            soup = Soup(MoyoStore.get(self, shop_url=url))            items = soup.find_element(class_='catalog_content').find(class_='catalog_products js-products-list')            self.all_items.append(items)    def generate_info_from_moyo(self):        for items in self.all_items:            if items:                for item in items.find_all('div', class_='product-card goods-item add-data ga-item'):                    name = item.find('a', class_='product-card_title gtm-link-product').text                    price = (item.find('div', class_='product-card_price_current').text.strip().replace(" ", "")                             .replace("â‚´", ""))                    card_item = {                        'name': name,                        'price': clean_price(price),                        'article': get_article_from_title(name).strip()                    }                    self.item_list.append(card_item)        return self.item_list