import osimport djangofrom redis_client.redis_client import redis_clientfrom stores.Portativ.portativ_info import PORTATIV_ARTICLESfrom stores.Portativ.portativ_model import PortativStoreos.environ.setdefault("DJANGO_SETTINGS_MODULE", "scraper.settings")django.setup()from items.models import Itemdef start_portativ_wacom():    portativ = PortativStore(url='https://portativ.ua/ua/catalogsearch/result/?q=wacom')    items = portativ.generate_info()    result = portativ.compare_data(items)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='portativ_wacom', value=json_data)    return resultdef start_portativ_xp_pen():    portativ = PortativStore(url='https://portativ.ua/ua/catalogsearch/result/?q=xp-pen')    items = portativ.generate_info()    result = portativ.compare_data_xp_pen(items, PORTATIV_ARTICLES, model=Item)    json_data = redis_client.transfer_to_json(result)    redis_client.save_data_in_redis(name='portativ_xp_pen', value=json_data)    return result